import './App.css';
import React from "react"
import "./App.css"
class App extends React.Component {

  constructor(){
    super()

    this.state = {
        firstName: '',
        lastName: "",
        email: "",
        placeOfBirth: "",
        phone: "",
        favoriteFood: "",
        aboutYou: "",
        lists: [] // an array of objects, where each object represents a name badge entry
        //Every
      }

      /*

      lists: [ 
        {
          firstName: '',
          lastName: "",
          email: "",
          placeOfBirth: "",
          phone: "",
          favoriteFood: "",
          aboutYou: "", 
        }, 
        {
          firstName: '',
          lastName: "",
          email: "",
          placeOfBirth: "",
          phone: "",
          favoriteFood: "",
          aboutYou: "", 
        }


      ]

      */


    this.handleChange = this.handleChange.bind(this)
    this.handleClick = this.handleClick.bind(this)
    this.handleLaziness = this.handleLaziness.bind(this)

  }


  handleChange(event){
    const{name, value} = event.target
    this.setState({ 
    [name]:value

  })

  }
  handleLaziness(e){
    const button = document.querySelector('.button')
    if(this.state.firstName.length > 2) {
      button.disabled = false
      return e.preventDefault()
    }
    if(this.state.firstName === "" || this.state.lastName === ""|| this.state.email === "" || this.state.placeOfBirth === "" || this.state.phone === "" || this.state.favoriteFood === "" || this.state.aboutYou === ""||this.state.firstName < 2 || this.state.lastName < 2 || this.state.email < 2 || this.state.placeOfBirth < 2 || this.state.phone < 2 || this.state.favoriteFood > 2 || this.state.aboutYou.length > 2) {
      return button.disabled = true
    }
  }

  handleClick(event){
    event.preventDefault() 
    this.setState(
      function(prevState){
      return {
        firstName: '',
        lastName: "",
        email: "",
        placeOfBirth: "",
        phone: "",
        favoriteFood: "",
        aboutYou: "", 
        lists: [...prevState.lists, 
          {
            firstName: prevState.firstName,
            lastName: prevState.lastName,
            email: prevState.email,
            placeOfBirth: prevState.placeOfBirth,
            phone: prevState.phone,
            favoriteFood: prevState.favoriteFood,
            aboutYou: prevState.aboutYou
          }]


      } //Added hi here to see if my map method below was working at all. It's not part of my real code.
    })
  }


  render(){
    const mappedList = this.state.lists.map(
      function(item){
        return(
          <div className="badgewrapper">
              <div className="box1">
                <h4>Name: {item.firstName}</h4>
              </div>

              <div className="box2">
                <h4>Last Name: {item.lastName}</h4>
              </div>

              <div className="box3">
                <h4>Place Of Birth: {item.placeOfBirth}</h4>
              </div>

              <div className="box4">
                <h4>Phone:  {item.phone}</h4>
              </div>

              <div className="box5">
                <h4>Favorite Food:  {item.favoriteFood}</h4>
              </div>

              <div className="box6">
                <h4>About:  {item.aboutYou}</h4>
              </div>
          </div>
        )
      }
    )
    return (
      <div>
        <div class="formwrapper">
        <form>
          <input
          class="input first-name"
          type="text"
          name="firstName"
          value={this.state.firstName}
          placeholder="First Name"
          onChange={this.handleChange}
          />
          <input
          class="input last-name"
          type="text"
          name="lastName"
          value={this.state.lastName}
          placeholder="Last Name" 
          onChange={this.handleChange}
          />
          <input
          class="input email"
          type="text"
          name="email"
          value={this.state.email}
          placeholder="Email" 
          onChange={this.handleChange}
          />
          <input
          class="input place"
          type="text"
          name="placeOfBirth"
          value={this.state.placeOfBirth}
          placeholder="Place of Birth" 
          onChange={this.handleChange}
          />
          <input
          class="input phone"
          type="text"
          name="phone"
          value={this.state.phone}
          placeholder="Phone" 
          onChange={this.handleChange}
          />
          <input
          class="input favorite"
          type="text"
          name="favoriteFood"
          value={this.state.favoriteFood}
          placeholder="Favorite Food" 
          onChange={this.handleChange}
          />
          <textarea
          class="input about-you"
          name="aboutYou"
          value={this.state.aboutYou}
          placeholder="Tell Me About You" 
          onChange={this.handleChange}
          />
          <button disabled={this.handleLaziness} className='button' onClick={this.handleClick}>Click Me</button> 
        </form>

        </div>

        <br />
        <br />
          {mappedList}
       <br />
      </div>
    )

  }

}

export default App;
