import './App.css';
import React from "react"
class App extends React.Component {

  constructor(){
    super()
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      placeOfBirth: "",
      phone: "",
      favoriteFood: "",
      aboutYou: "",
      lists: [this.firstName, this.lastName, this.email, this.placeOfBirth, this.phone, this.favoriteFood, this.aboutYou]

    }
    this.handleChange = this.handleChange.bind(this)
  }


  handleChange(event){
      const{name, value} = event.target
        this.setState({ 
        [name]:value

      })}

  handleClick(){
    this.setState(
      function(prevState) {
      return [...this.state.lists, prevState]  
    })
  }  
  render(){
    const mappedList = this.lists.map(
      function(item){
        return(
          <li>
            {item}
          </li>
        )
      }
    )
    return (
      <div>
        <form>
          <input
          type="text"
          name="firstName"
          value={this.state.firstName}
          placeholder="First Name"
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="lastName"
          value={this.state.lastName}
          placeholder="Last Name" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="email"
          value={this.state.email}
          placeholder="Email" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="placeOfBirth"
          value={this.state.placeOfBirth}
          placeholder="Place of Birth" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="phone"
          value={this.state.phone}
          placeholder="Phone" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="favoriteFood"
          value={this.state.favoriteFood}
          placeholder="Favorite Food" 
          onChange={this.handleChange}
          />
          <textarea
          name="aboutYou"
          value={this.state.aboutYou}
          placeholder="Tell Me About You" 
          onChange={this.handleChange}
          />
          <button onClick={this.handleClick}>Click Me</button>
        </form>
        {mappedList}
      </div>
    )

  }

}

export default App;
