import './App.css';

class App extends React.Component {

  constructor(){
    super()
  }

  handleChange(event){
      const{name, value, type,} = event.target
        this.setState({ 
        [name]:value

      })}

  render(){
    return (
      <div>
        <form>
          <input
          type="text"
          name="firstName"
          value={this.state.name}
          placeholder="First Name"
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="lastName"
          value={this.state.name}
          placeholder="Last Name" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="firstName"
          value={this.state.name}
          placeholder="First Name" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="email"
          value={this.state.name}
          placeholder="Email" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="placeOfBirth"
          value={this.state.name}
          placeholder="Place of Birth" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="phone"
          value={this.state.name}
          placeholder="Phone" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="favoriteFood"
          value={this.state.name}
          placeholder="Favorite Food" 
          onChange={this.handleChange}
          />
          <textarea
          name="aboutYou"
          value={this.state.name}
          placeholder="Tell Me About You" 
          onChange={this.handleChange}
          />
          <button>Click Me</button>
        </form>
      </div>
    )

  }

}

export default App;
