import './App.css';
import React from "react"
class App extends React.Component {

  constructor(){
    super()

    this.state = {
        firstName: '',
        lastName: "",
        email: "",
        placeOfBirth: "",
        phone: "",
        favoriteFood: "",
        aboutYou: "",
        lists: [] // an array of objects, where each object represents a name badge entry
        //Every
      }

      /*

      lists: [ 
        {
          firstName: '',
          lastName: "",
          email: "",
          placeOfBirth: "",
          phone: "",
          favoriteFood: "",
          aboutYou: "", 
        }, 


      ]

      */


    this.handleChange = this.handleChange.bind(this)
    this.handleClick = this.handleClick.bind(this)
  }


  handleChange(event){
      const{name, value} = event.target
        this.setState({ 
        [name]:value

      })}

  handleClick(event){
    event.preventDefault() 
    this.setState(
      function(prevState){
      return{lists: [...prevState.lists, "hi"]} //Added hi here to see if my map method below was working at all. It's not part of my real code.
    })
  }  
  render(){
    const mappedList = this.state.lists.map(
      function(item){
        return(
          <li>
            {item}
          </li>
        )
      }
    )
    return (
      <div>
        <form>
          <input
          type="text"
          name="firstName"
          value={this.state.firstName}
          placeholder="First Name"
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="lastName"
          value={this.state.lastName}
          placeholder="Last Name" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="email"
          value={this.state.email}
          placeholder="Email" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="placeOfBirth"
          value={this.state.placeOfBirth}
          placeholder="Place of Birth" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="phone"
          value={this.state.phone}
          placeholder="Phone" 
          onChange={this.handleChange}
          />
          <input
          type="text"
          name="favoriteFood"
          value={this.state.favoriteFood}
          placeholder="Favorite Food" 
          onChange={this.handleChange}
          />
          <textarea
          name="aboutYou"
          value={this.state.aboutYou}
          placeholder="Tell Me About You" 
          onChange={this.handleChange}
          />
          <button onClick={this.handleClick}>Click Me</button>
        </form>
        <ul className="listCentral">
          {mappedList}
        </ul>
      </div>
    )

  }

}

export default App;
